import React from 'react';
import { MDBContainer, MDBRow, MDBCol, MDBInput, MDBBtn, MDBIcon} from 'mdbreact';
import $ from 'jquery';

class Contact extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            name: '',
            email: '',
            subj: '',
            message: ''
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }

    render() {
        return (
            <div className="main-contact pb-5 pt-5">
                <MDBContainer>
                    <MDBRow>
                        <MDBCol md="6" className="mb-5 mb-lg-0">
                            <h4 className="text-center mb-4 font-weight-bold">CONTACT INFO</h4>                            
                            <p className=""><span className="font-weight-bold">Address: </span> San Pablo City, Laguna</p>
                            <p className=""><span className="font-weight-bold">Phone Number: </span> 00000000</p>
                            <p className=""><span className="font-weight-bold">Email Address: </span> janedreforma@gmail.com</p>
                            <p className=""><span className="font-weight-bold">Website: </span> https://jane-reforma-cv.herokuapp.com/</p>
                        </MDBCol>
                        <MDBCol md="6">
                            <form id="contact-form">
                                <h4 className="text-center mb-4 font-weight-bold">CONTACT FORM</h4>
                                    <div className="grey-text">
                                        <MDBInput label="Your name" icon="user" id="test-mailing" name="test-mailing"group type="text" validate error="wrong" success="right" />
                                        <MDBInput label="Your email" icon="envelope" group type="email" value={this.state.email} validate error="wrong" success="right"/>
                                        <MDBInput label="Subject" icon="tag" group type="text" value={this.state.subj} validate error="wrong" success="right" />
                                        <MDBInput type="textarea" rows="2" label="Your message" icon="pencil-alt" value={this.state.message} />
                                    </div>
                                    <div className="text-center">
                                    <MDBBtn color="elegant" type="submit">
                                        Send <MDBIcon icon="paper-plane" className="ml-1" />
                                    </MDBBtn>
                                    </div>
                            </form>
                        </MDBCol>
                    </MDBRow>
                </MDBContainer>
            </div>
        );
    }    

    handleChange(event) {
        this.setState({feedback: event.target.value})
    }

      handleSubmit (event) {
        const templateId = 'template_PAre7b0C';
        this.sendFeedback(templateId, {message_html: this.state.name, from_name: this.state.name, reply_to: this.state.email})
      }

      sendFeedback(e) {
        var data = {
            service_id: 'gmail',
            template_id: 'template_PAre7b0C',
            user_id: 'user_9dj8XnIPMrkgsAzS2sYBa'
        };

        $.ajax('https://api.emailjs.com/api/v1.0/email/send', {
            type: 'POST',
            data: JSON.stringify(data),
            contentType: 'application/json'
        }).done(function() {
            alert('Your mail is sent!');
        }).fail(function(error) {
            alert('Oops... ' + JSON.stringify(error));
        });
      }

}

export default Contact;
