import React from 'react';
import { MDBContainer, MDBRow, MDBCol, MDBInput, MDBBtn, MDBIcon} from 'mdbreact';
import $ from 'jquery';

class Contact extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            name: '',
            email: '',
            message: ''
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }

    render() {
        return (
            <div className="main-contact pb-5 pt-5">
                <MDBContainer>
                    <MDBRow>
                        <MDBCol md="6" className="mb-5 mb-lg-0">
                            <h4 className="text-center mb-4 font-weight-bold">CONTACT INFO</h4>                            
                            <p className=""><span className="font-weight-bold">Address: </span> San Pablo City, Laguna</p>
                            <p className=""><span className="font-weight-bold">Phone Number: </span> 00000000</p>
                            <p className=""><span className="font-weight-bold">Email Address: </span> janedreforma@gmail.com</p>
                            <p className=""><span className="font-weight-bold">Website: </span> https://jane-reforma-cv.herokuapp.com/</p>
                        </MDBCol>
                        <MDBCol md="6">
                            <form id="contact-form" onSubmit={this.handleSubmit.bind(this)} method="POST">
                                <h4 className="text-center mb-4 font-weight-bold">CONTACT FORM</h4>
                                    <div className="grey-text">
                                        <MDBInput label="Your name" icon="user" id="test-mailing" name="test-mailing" value={this.state.name} group type="text" validate error="wrong" success="right" />
                                        <MDBInput label="Your email" icon="envelope" group type="email" value={this.state.email} validate error="wrong" success="right"/>
                                        <MDBInput label="Subject" icon="tag" group type="text" validate error="wrong" success="right" />
                                        <MDBInput type="textarea" rows="2" label="Your message" icon="pencil-alt" />
                                    </div>
                                    <div className="text-center">
                                    <MDBBtn color="elegant" onClick={this.handleSubmit}>
                                        Send <MDBIcon icon="paper-plane" className="ml-1" />
                                    </MDBBtn>
                                    </div>
                            </form>
                              	<form className="test-mailing">
                                    <h1>Let's see if it works</h1>
                                    <div>
                                    <textarea
                                        id="test-mailing"
                                        name="test-mailing"
                                        onChange={this.handleChange}
                                        placeholder="Post some lorem ipsum here"
                                        required
                                        value={this.state.feedback}
                                        style={{width: '100%', height: '150px'}}
                                    />
                                    </div>
                                    <input type="button" value="Submit" className="btn btn--submit" onClick={this.handleSubmit} />
  	                            </form>
                        </MDBCol>
                    </MDBRow>
                </MDBContainer>
            </div>
        );
    }    
    handleChange(event) {
        this.setState({feedback: event.target.value})
      }

    handleSubmit (event) {
    const templateId = 'template_PAre7b0C';

    this.sendFeedback(templateId, {message_html: this.state.feedback, from_name: this.state.name, reply_to: this.state.email})
    }

    sendFeedback (templateId, variables) {
    window.emailjs.send(
        'gmail', templateId,
        variables
        ).then(res => {
        console.log('Email successfully sent!')
        })
        // Handle errors here however you like, or use a React error boundary
        .catch(err => console.error('Oh well, you failed. Here some thoughts on the error that occured:', err))
    }
}

export default Contact;
