import React from 'react';
import { MDBNavbar, MDBNavbarBrand, MDBNavbarNav, MDBNavbarToggler, MDBCollapse, MDBNavItem, MDBNavLink, MDBMask, MDBBtn, MDBView, MDBAnimation} from 'mdbreact';

class Header extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        collapse: false,
        isWideEnough: false,
      };
      this.onClick = this.onClick.bind(this);
    }

    onClick() {
      this.setState({
        collapse: !this.state.collapse,
      });
    }

    render() {

      if(this.props.data) {
          var greetings = this.props.data.greetings;
          var name = this.props.data.name;
          var occupation= this.props.data.occupation;
          var networks= this.props.data.social.map(function(network){
              return <li key={network.name}><a href={network.url}><i className={network.className}></i></a></li>
          })
      }

      return (
        <div className="main-wrapper">
          <header className="mainHeader">


            <MDBView src="/images/bg.jpg">
                <MDBMask overlay="black-strong" className="flex-center flex-column text-white text-center">
                    <span className="pb-3">{greetings}</span>  
                    <h1>I am {name}</h1>
                    <h5 className="">{occupation}</h5>
                    <hr />
                    <ul className="social pb-3">
                        {networks}
                    </ul>

                    <MDBAnimation type="" infinite>
                        <p className="scrolldown scroll-more pt-3">
                            <a className="smoothscroll" href="#section1"><i className="fa fa-arrow-down" aria-hidden="true"></i></a>
                        </p>
                    </MDBAnimation>
                    {/* <MDBBtn className="smoothscroll" href="#section1" rounded outline color="warning">Explore More</MDBBtn> */}
                </MDBMask>
            </MDBView>
          </header>
        </div>
      );
    }
  }

  export default Header;
